pragma solidity ^0.5.0;
import './BusinessContract.sol';

contract ProxyContract {
  // Proxyコントラクトには変数を定義しない
  uint count;
  mapping ( uint => uint ) point;
  address businessContract;

  constructor() public {
  }

  function setBusinessContract(address _address) public {
    businessContract = _address;
  }

  function delegateCallBusiness(string memory _method, uint256[] memory _arugument) public returns(bool, bytes memory) {
    bool status;
    bytes memory result;
    (status, result) = businessContract.delegatecall(abi.encodePacked(bytes4(keccak256(bytes(_method))), _arugument));
    return (status, result);
  }


  // get data by slot id
  // slot idをクライアントが知る術がないので非推奨
  function get(uint slot_id) public view returns (uint){
    bytes32 r;
    assembly {
      r := sload(slot_id) //we load the slot number i
    }
    return uint(r);
  }

}
